import React, { useEffect, useState } from 'react'
import './style.css';
import FavoriteItem from '../../../Components/FavoriteItem';
import { FavoriteListMock, commentListMock } from '../../../Mocks';
import favoriteListItem from '../../../Types/Interface/Favorite-List-Item.interface';
import { Board, commentListItem } from '../../../Types/Interface';
import CommentItem from '../../../Components/CommentItem';
import Pagination from '../../../Components/Pagination';
import { useLoginUserStore } from '../../../Stores';
import { useNavigate, useParams } from 'react-router-dom';
import { BOARD_PATH, BOARD_UPDATE_PATH, USER_PATH } from '../../../Constants';
import defaultProfileImage from '../../../Assets/Image/default-profile-image.png'
import boardMock from '../../../Mocks/Board.mock';


// component: 게시물 상세화면 컴포넌트 
export default function BoardDetail() {
// state: 게시물 번호 path variable 상태 
const {boardNumber}=useParams();
// state: 로그인 유저 상태 
const{loginUser}=useLoginUserStore();

// function: 네비게이트 함수 
const navigator=useNavigate();



// component: 게시물 상세 상단 컴포넌트 
const BoardDetailTop =()=>{

// state: 
const [board,setBoard]=useState<Board |null>(null);
// state: more 버튼 상태 
const [showMore,setShowMore]=useState<boolean>(false);

//event handler: 닉네임 클릭 이벤트 처리 
const onNicknameClickHandler=()=>{
  if(!board) return;

  navigator(USER_PATH(board.writerEmail));
}

//event handler: more 버튼 클릭 이벤트 처리 
const onMoreButtonClickHandler=()=>{
  setShowMore(!showMore)
}
//event handler: 업데이트 버튼 클릭 이벤트 처리 
const onUpdateButtonClickHandler=()=>{
  if(!board ||!loginUser)return;
  if(loginUser.email !==board.writerEmail)return;
  navigator(BOARD_PATH()+'/'+BOARD_UPDATE_PATH(boardNumber));
}
// effect: 게시물 번호 path variable이 바뀔때마다 게시물 불러오기 
useEffect(()=>{
  setBoard(boardMock)
},[boardNumber])


// render: 게시물 상세 상단 컴포넌트 렌더링 
if(!board) return <></>

  return(
    <div id='board-detail-top'>
        <div className='board-detail-top-header'>
              <div className='board-detail-title'>{board.title}</div>
              <div className='board-detail-top-sub-box'>
                 <div className='board-detail-write-info-box'>
                      <div className='board-detail-writer-profile-image' style={{backgroundImage:`url(${board.writerProfileImage? board.writerProfileImage:defaultProfileImage})`}}></div>
                      <div className='board-detail-writer-nickname' onClick={onNicknameClickHandler}>{board.writerNickname}</div>
                      <div className='board-detail-info-divider'>{'\|'}</div>
                      <div className='board-detail-write-date'>{board.writeDatetime}</div>
                 </div>
                 <div className='Icon-Button' onClick={onMoreButtonClickHandler}>
                      <div className='Icon more-icon'></div>
                 </div>
                 {showMore &&
                 <div className='board-detail-more-box'>
                      <div className='board-detail-update-button'>{'수정'}</div>
                      <div className='divider'></div>
                      <div className='board-detail-delete-button'>{'삭제'}</div>
                 </div>}
              </div>
        </div>
        <div className='divider'></div>
        <div className='board-detail-top-main'>
            <div className='board-detail-main-text'>{board.content}</div>
            {board.boardImageList.map(image=><img className='board-detail-main-image'src={image}/>)}

        </div>



    </div>
  );
}



// component: 게시물 상세 하단 컴포넌트 
const BoardDetailBottom=()=>{

const [favoriteList,setFavoriteList]=useState<favoriteListItem[]>([]);
const [commentList, setCommentList]=useState<commentListItem[]>([]);
useEffect(()=>{
setFavoriteList(FavoriteListMock);
setCommentList(commentListMock);
},[])


// render: 게시물 상세 하단 컴포넌트 렌더링 
  return(
    <div id='board-detail-bottom'>
    <div className='board-detail-bottom-button-box'>
      <div className='board-detail-bottom-button-group'>
        <div className='Icon-Button'>
          <div className='Icon favorite-fill-icon'></div>
        </div>
        <div className='board-detail-bottom-button-text'>{`좋아요${12}`}</div>
        <div className='Icon-Button'>
          <div className='Icon up-light-icon'></div>
        </div>
      </div>
      <div className='board-detail-bottom-button-group'>
        <div className='Icon-Button'>
          <div className='Icon comment-icon'></div>
        </div>
        <div className='board-detail-bottom-button-text'>{`댓글${12}`}</div>
        <div className='Icon-Button'>
          <div className='Icon up-light-icon'></div>
        </div>
      </div>
    </div>
    <div className='board-detail-bottom-favorite-box'>
          <div className='board-detail-bottom-favorite-container'>
              <div className='board-detail-bottom-favorite-title'>{'좋아요'}<span className='emphapsis'>{12}</span></div>
              <div className='board-detail-bottom-favorite-contents'>
                {favoriteList.map(a=><FavoriteItem FavoriteListItem={a}/>)}
              </div>
          </div>
    </div>
    <div className='board-detail-bottom-comment-box'>
      <div className='board-detail-bottom-comment-container'>
        <div className='board-detail-bottom-comment-title'>{'댓글'}<span className='emphasis'>{12}</span></div>
        <div className='board-detail-bottom-list-container'>
        {commentList.map(a=><CommentItem commentListItem={a}/>)}
        </div>
      </div>
      <div className='divider'></div>
      <div className='board-detail-bottom-pagination-box'>
        <Pagination/>
      </div>
      <div className='board-detail-bottom-comment-input-box'>
        <div className='board-detail-bottom-comment-input-container'>
            <textarea className='board-detail-bottom-comment-textarea' placeholder='댓글을 작성해주세요.'/>
            <div className='board-detail-bottom-comment-button-box'>
              <div className='disable-button'>{'댓글 달기'}</div>
            </div>
        </div>
      </div>
    </div>
    </div>
  );
};



// render: 게시물 상세화면 컴포넌트 렌더링 
  return (
    <div id='board-detail-wrapper'>
      <div className='board-detail-container'>
          <BoardDetailTop/>
          <BoardDetailBottom/>
      </div>
    </div>
  )
}
