import React, { useEffect,useState} from 'react'
import './style.css';
import { useParams } from 'react-router-dom';
import { BoardListItem } from '../../Types/Interface';
import { imageche } from '../../Mocks';
import BoardItem from '../../Components/BoardItem';



// component: 검색화면 컴포넌트 
export default function Search() {
// state: searchWord path variable 상태 
const {searchWord}=useParams();

//state: 검색 게시물 리스트 상태 (임시)
const [searchBoardList,setSearchBoardList]=useState<BoardListItem[]>([]);
//state: 검색 게시물 개수 상태 
const [count,setCount]=useState

//effect: 첫 마운트 시 실행될 함수 
useEffect(()=>{
  setSearchBoardList(imageche);
},[])
// render: 검색화면 컴포넌트 렌더링 
if(!searchWord) return (<></>)
  return (
    <div id='search-wrapper'>
      <div className='search-container'>
         <div className='search-title-box'>
            <div className='search-title'><span className='emphasis'>{searchWord}</span>{'에 대한 검색결과 입니다.'}</div>
            <div className='search-count'>{0}</div>
         </div>
         <div className='search-contents-box'>
            <div className='search-contents'>
              {searchBoardList.map(boardListItem=><BoardItem boardListItem={boardListItem}/>)}
            </div>
            <div className='search-relation-box'>
              <div className='search-relation-card'>
                <div className='search-relation-card-container'>
                    <div className='search-relation-card-title'></div>
                    <div className='search-relation-card-contents'></div>
                </div>
              </div>
            </div>
         </div>
         <div className='search-pagination-box'></div>
      </div>
    </div>
  )
}
