import React, { useEffect, useState } from 'react'
import './style.css';
import Top3Item from '../../Components/Top3Item';
import { BoardListItem } from '../../Types/Interface';
import { imageche } from '../../Mocks';
import BoardItem from '../../Components/BoardItem';
import Pagination from '../../Components/Pagination';
import { useNavigate } from 'react-router-dom';
import { SEARCH_PATH } from '../../Constants';
import { getTop3BoardListRequest } from '../../Apis';
import { GetTop3BoardListResponseDto } from '../../Apis/Response/Board';
import { ResponseDto } from '../../Apis/Response';
import { usePagination } from '../../Hooks';




// component: 메인화면 컴포넌트 
export default function Main() {




  // component: 메인화면 상단 컴포넌트 
  const MainTop =()=>{
  //state: 주간 top3 게시물 리스트 상태(임시) 
  const[top3BoardList,setTop3BoardList]=useState<BoardListItem[]>([]);

  //function: get top 3 board list response 처리 함수 
  const getTop3BoardListResponse =(responseBody:GetTop3BoardListResponseDto |ResponseDto|null)=>{
    if(!responseBody) return;
    const {code}=responseBody;
    if(code ==='DBE') alert('데이터베이스 오류 입니다.');
    if(code !=='SU') return;

    const {top3List}=responseBody as GetTop3BoardListResponseDto;
    setTop3BoardList(top3List);
  }
  // effect: 첫 마운트 시 실핼될 함수 
  useEffect(()=>{
    getTop3BoardListRequest().then(getTop3BoardListResponse);
  },[])
  // render: 메인화면 상단 컴포넌트 렌더링 
  return(
    <div id='main-top-wrapper'>
        <div className='main-top-container'>
            <div className='main-top-title'>{'김채원 팬클럽에서\n다양한 이야기를 나눠보세요'}</div>
            <div className='main-top-contents-box'>
                <div className='main-top-contents-title'>{'주간 TOP 3 게시물'}</div>
                <div className='main-top-contents'>
                  {top3BoardList.map(top3ListItem=><Top3Item Top3ListItem={top3ListItem}/>)}
                </div>
            </div>
        </div>
    </div>
  )
}







  // component: 메인화면 하단 컴포넌트 
  const MainBottom =()=>{

  // function: 네비게이트 함수 
  const navigate=useNavigate();

  //state: 페이지 네이션 관련 상태 
  const {}=usePagination
  //state: 인기 검색어 리스트 상태 
  const[popularWordList,setPopularWordList]=useState<string[]>([]);

  //event handler: 인기 검색어 클릭 이벤트 처리 
  const onPopularWordClickHandler=(word:string)=>{
    navigate(SEARCH_PATH(word));
  }
  // effect: 첫 마운트 시 실핼될 함수 
  useEffect(()=>{
    setCurrentBoardList(imageche);
    setPopularWordList(['김채원','르세라핌'])
  },[])
  // render: 메인화면 하단 컴포넌트 렌더링 
  return(
      <div id='main-bottom-wrapper'>
        <div className='main-bottom-container'>
          <div className='main-bottom-title'>{'최신 게시물'}</div>
          <div className='main-bottom-contents-box'>
                <div className='main-bottom-current-contents'>
                  {currentBoardList.map(boardListItem =><BoardItem boardListItem={boardListItem}/>)}

                </div>
                <div className='main-bottom-popular-box'>
                          <div className='main-bottom-popular-card'>
                              <div className='main-bottom-popular-card-container'>
                                  <div className='main-bottom-popular-card-title'>{'인기 검색어'}</div>
                                  <div className='main-bottom-popular-card-contents'>
                                    {popularWordList.map(word=><div className='word-badge' onClick={()=>onPopularWordClickHandler(word)}>{word}</div>)}
                                  </div>
                              </div>
                          </div>
                </div>
          </div>
          <div className='main-bottom-pagination-box'>
            {/*<Pagination/>*/}
          </div>
        </div>
      </div>
  )
  }






// render: 메인화면 컴포넌트 렌더링 
  return (
    <>
     <MainTop/>
     <MainBottom/>
    </>
  )
}
