export default function Container() {
// state: 현재 페이지 path name 상태 

const {pathname}=useLocation();

// render: 컨테이너 레이아웃 렌더링 
  return (
    < >
      <Header />
      <Outlet/>
      {pathname !=='auth'&& <Footer/>}

    </>
  )
}
