import React, { useEffect, useRef, useState } from 'react'
import './style.css';
import { User } from '../../Types/Interface';
import defaultProfileImage from '../../Assets/Image/default-profile-image.png';
import { useParams } from 'react-router-dom';

// component: 유저화면 컴포넌트 
export default function UserPage() {

const {userEmail}=useParams();








// component: 유저화면 상단 컴포넌트 
const UserTop=()=>{
// state: 이미지 파일 인풋 참조 상태 
const imageInputRef=useRef<HTMLInputElement |null>(null);
// state: 마이페이지 여부 상태 
const [isMyPage,setMyPage]=useState<boolean>(true);
// state: 닉네임 변경 여부 상태 
const [isChangeNickname,setNickname]=useState<boolean>(true);
// state: user 정보 상태 
const [user,setUser]=useState<User |null>(null);

// effect: user email path variable 변경시 실핼 할 함수 
useEffect(()=>{
  if(userEmail) return;
  setUser({email:'email@email.com',nickname:'나는정재호',profileImage:'https://i.namu.wiki/i/bw9-S8n2s9Wz6eVTye7eu_xO0dX8toZ6lsi6j61JzBJX-lZ-Gq6ID7s4oGypitZ0ua2ili9GFyfspzl2JLwQWIQPpX3gvlZsi2sSLS2gZI2X28fQWwx9QLUAhDT_d8zEAt8qoCblPwE4rGrO0yJjVw.webp'})
},[userEmail])
// render: 유저화면  상단 컴포넌트 렌더링 
if(!user) return (<></>)
return(
  <div id='user-top-wrapper'>
    <div className='user-top-container'>
      {isMyPage ? <div className='user-top-my-profile-image-box'>
                    {user.profileImage !==null ?
                        <div className='user-top-profile-image' style={{backgroundImage:`url(${user.profileImage})`}}></div>:
                        <div className='user-top-my-profile-image-nothing-box'>
                             <div className='Icon-Box-large'>
                             <div className='Icon image-box-white-icon'></div>
                             </div>
                        </div>
                    }
                        <input ref={imageInputRef} type='file' accept='image/*' style={{display:'none'}} />
                  </div>:
                  <div className='user-top-profile-image-box' style={{backgroundImage:`url(${user.profileImage? user.profileImage:defaultProfileImage})`}}></div>
      }
          <div className='user-top-info-box'>
                  <div className='user-top-info-nickname-box'>
                    {isMyPage ?
                    <>
                    <div className='user-top-info-nickname'>{user.nickname}</div>
                    <div className='Icon-Button'>
                      <div className='Icon edit-icon'></div>
                    </div>
                    </> :
                    <div className='user-top-info-nickname'>{user.nickname}</div>
                    }

                  </div>
                  <div className='user-top-info-email'>{'email@email.com'}</div>
          </div>
    </div>
  </div>
)

}


// component: 유저화면 하단 컴포넌트 
const UserBottom=()=>{

  // render: 유저화면  히딘 컴포넌트 렌더링 
  return(
    <div></div>
  )

  }










// render: 유저화면 컴포넌트 렌더링 
  return (
    <>
      <UserTop/>
      <UserBottom/>
    </>
  )
}
