import React, { ChangeEvent, useState } from 'react';
import './style.css';
import { useNavigate } from 'react-router-dom';
import { MAIN_PATH, SEARCH_PATH } from '../../Constants';



// component: 헤더 레이아웃 
export default function Header() {
// function: 네비게이트 함수

const navigate=useNavigate();

// event handler: 로고 클릭 이벤트 처리 함수 

const onLogoClickHandler=()=>{
  navigate(MAIN_PATH());
}

// component: 검색 버튼 컴포넌트 

const SearchButton=()=>{

// state: 검색 버튼 상태 
const[status,setStatus]=useState<Boolean>(false);
//state; 검색어 상태 
const[searchWord,setSearchWord]=useState<string>('');
// event handler: 검색 버튼 클릭 이벤트 처리 함수 
const onSearchWordHandler=(event:ChangeEvent<HTMLInputElement>)=>{

    const value=event.target.value;
    setSearchWord(value); 
};

// event handler: 검색 버튼 클릭 이벤트 처리 함수 
const onSearchClickHandler=()=>{
  if(!status){
    setStatus(!status);
    return;
  }
  navigate(SEARCH_PATH(searchWord))
}
//status는 현재 검색 버튼의 상태를 나타냄. false인 경우 검색 버튼이 보이고, true인 경우 검색 입력 상자가 보인다
if (!status)
// render: 검색버튼 컴포넌트 랜더링(클릭 x false)
return (<div className='Icon-Button' onClick={onSearchClickHandler}>
              <div className='Icon serch-light-icon'></div>
        </div>
       );
// render: 검색버튼 컴포넌트 랜더링(클릭 o true) 
return(
  <div className='Header-Search-Input-Box'>
        <input className='Hedaer-Search-Input'type='text' placeholder='검색어를 입력해주세요.' value={searchWord} onChange={onSearchWordHandler}/>
        <div className='Icon-Button' onClick={onSearchClickHandler}>
             <div className='Icon serch-light-icon'>
             </div>
        </div>

  </div>
)
}
// render: 헤더 레이아웃 렌더링 
  return (
    <div id= 'Header'>
      <div className='Header-Container'>
            <div className='Header-Left-Box' onClick={onLogoClickHandler}>
                  <div className='Icon-Box'>
                      <div className='Icon Logo-Dark-Icon'></div>
                  </div>
                  <div className='Header-Logo'>{'Jaehos Board'}</div>
            </div>
            <div className='Header-Right-Box'>
             <SearchButton/>
            </div>
      </div>

    </div>
  )
}
