import React, { useState } from 'react'
import './style.css';
import InputBox from '../../Components/InputBox/Index';



// component: 인증화면 컴포넌트 
export default function Authentication() {
// state: 화면 상태 (로그인,회원가입에 따른) 
const[view,setView]=useState<'sign-in'|'sign-up'>('sign-in');




// component: sign in card 컴포넌트 
const SignInCard=()=>{
// render: sign in card 컴포넌트 렌더링 
  return(
    <div className='auth-card'>
          <div className='auth-card-box'>
                <div className='auth-card-top'>
                      <div className='auth-card-title-box'>
                          <div className='auth-card-title'>로그인</div>
                      </div>
                      <InputBox label={''} type={'text'} placeholder={''} error={false} value={''} setValue={function (value: React.SetStateAction<string>): void {
            throw new Error('Function not implemented.');
          } }/>
                      <InputBox label={''} type={'text'} placeholder={''} error={false} value={''} setValue={function (value: React.SetStateAction<string>): void {
            throw new Error('Function not implemented.');
          } }/>
                </div>
                <div className='auth-card-bottom'>
                      <div className='auth-sign-in-error-box'>
                            <div className='auth-sign-in-error-message'>{'이메일 주소 또는 비밀번호를 잘못 입력했습니다 \n 입력하신 내용을 다시 확인해주세요.'}</div>
                      </div>
                      <div className='black-large-full-button'></div>
                      <div className='auth-description-box'>
                            <div className='auth-description'>{'신규 사용자이신가요?'}<span className='auth-description-link'>{'회원가입'}</span></div>
                      </div>
                </div>
          </div>
    </div>
  )
};

// component: sign up card 컴포넌트 
const SignUpCard=()=>{
// render: sign up card 컴포넌트 렌더링 
  return(
    <div className='auth-card'></div>
  )
};






// render: 인증화면 컴포넌트 렌더링 
  return (
    <div id='auth-wrapper'>
      <div className='auth-container'>
                <div className='auth-jumbotron-box'>
                        <div className='auth-jumbotron-contents'>
                                <div className='auth-logo-icon'></div>
                                <div className='auth-jumbotron-text-box'>
                                        <div className='auth-jumbotron-text'>{'환영합니다.'}</div>
                                        <div className='auth-jumbotron-text'>{'김채원 ❤️ 블로그'}</div>
                                </div>
                        </div>
                </div>
                {view=='sign-in'&&<SignInCard/>}
                {view=='sign-up'&&<SignUpCard/>}
      </div>

    </div>
  )
}
