import React, { ChangeEvent, useEffect, useRef, useState } from 'react'
import './style.css';

import defaultProfileImage from '../../Assets/Image/default-profile-image.png';
import { useParams } from 'react-router-dom';

// component: 유저화면 컴포넌트 
export default function User() {

const {userEmail}=useParams();








// component: 유저화면 상단 컴포넌트 
const UserTop=()=>{
// state: 이미지 파일 인풋 참조 상태 
const imageInputRef=useRef<HTMLInputElement |null>(null);
// state: 마이페이지 여부 상태 
const [isMyPage,setMyPage]=useState<boolean>(true);
// state: 닉네임 변경 여부 상태 
const [isNicknameChange,setNicknameChange]=useState<boolean>(false);
// state: 닉네임 상태 
const [nickname,setNickname]=useState<string>('');
// state: 변경 닉네임 상태 
const [changeNickname,setChangeNickname]=useState<string>('');
// state: 프로필 이미지 상태 
const [profileImage,setProfileImage]=useState<string |null>(null);

// event handler: 프로필 박스 클릭 이벤트 처리 
const onProfileBoxClickHandler =()=>{
  if(!isMyPage)return;
  if(!imageInputRef.current) return;
  imageInputRef.current.click();
}

// event handler: 닉네임 수정 버튼 클릭 이벤트 처리 
const onNicknameEditButtonClickHandler=()=>{
  setChangeNickname(nickname);
  setNicknameChange(!isNicknameChange);
}
// event handler: 프로필 이미지 변경 이벤트 처리 
const onProfileImageChangeHandler =(event:ChangeEvent<HTMLInputElement>)=>{
  if(!event.target.files ||!event.target.files?.length )return;
  const file=event.target.files[0];
  const data= new FormData();

  data.append('file',file);
}

// effect: user email path variable 변경시 실핼 할 함수 
useEffect(()=>{
  if(!userEmail) return;
  setNickname('나는정재호');
  setProfileImage('https://image.ytn.co.kr/osen/2022/12/227ee233-7c28-4a83-8f3f-5a2c67b4be03.jpg');

},[userEmail])
// render: 유저화면  상단 컴포넌트 렌더링 

return(
  <div id='user-top-wrapper'>
    <div className='user-top-container'>
      {isMyPage ? <div className='user-top-my-profile-image-box'onClick={onProfileBoxClickHandler}>
                    {profileImage !==null ?
                        <div className='user-top-profile-image' style={{backgroundImage:`url(${profileImage})`}}></div>:
                             <div className='Icon-Box-large'>
                             <div className='Icon image-box-white-icon'></div>
                             </div>

                    }
                        <input ref={imageInputRef} type='file' accept='image/*' style={{display:'none'}} onChange={oncha} />
                  </div>:
                  <div className='user-top-profile-image-box' style={{backgroundImage:`url(${profileImage?profileImage:defaultProfileImage})`}}></div>
      }
          <div className='user-top-info-box'>
                  <div className='user-top-info-nickname-box'>
                    {isMyPage ?
                    <>
                    {isNicknameChange ?
                    <input className='user-top-info-nickname-input' type='text' size={changeNickname.length + 2} value={changeNickname}/> :
                    <div className='user-top-info-nickname'>{nickname}</div>
                    }

                    <div className='Icon-Button 'onClick={onNicknameEditButtonClickHandler}>
                      <div className='Icon edit-icon'></div>
                    </div>
                    </> :
                    <div className='user-top-info-nickname'>{nickname}</div>
                    }

                  </div>
                  <div className='user-top-info-email'>{'email@email.com'}</div>
          </div>
    </div>
  </div>
)

}


// component: 유저화면 하단 컴포넌트 
const UserBottom=()=>{

  // render: 유저화면  히딘 컴포넌트 렌더링 
  return(
    <div></div>
  )

  }










// render: 유저화면 컴포넌트 렌더링 
  return (
    <>
      <UserTop/>
      <UserBottom/>
    </>
  )
}
